import React, { useState } from 'react';
import authService from '../services/authservice';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../context/authcontext';

function Logging() {
  const { isLoggedIn, setIsLoggedIn } = useAuth();
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // To store login errors
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const response = await authService.initialLogin(username, password);
      if (response.status === '2FA_required') {
        // Store user_id temporarily for 2FA verification
        sessionStorage.setItem('tempUserId', response.user_id);
        // Prompt user to enter 2FA code
        // You might want to replace this with a more sophisticated UI element
        const twoFaCode = prompt("Enter your 2FA code:");
        verify2FACode(twoFaCode);
      }
    } catch (error) {
      console.error("Initial login failed:", error);
      // Handle error
    }
};

const verify2FACode = async (code) => {
  const user_id = sessionStorage.getItem('tempUserId');
  try {
    const response = await authService.verify2FACode(user_id, code);
    if (response.token) {
      // Login successful
      localStorage.setItem('userToken', response.token);
      setIsLoggedIn(true);
      navigate('/user-profile');
    }
  } catch (error) {
    console.error("2FA verification failed:", error);
    // Handle error
  }
};

  const handleLogout = async () => {
    await authService.logout(); 
    setIsLoggedIn(false);
    localStorage.removeItem('userToken'); // Ensure token is removed on logout
    navigate('/home'); // Navigate to home page on logout
  };

  // Use the form's onSubmit for the login process
  const loginForm = (
    <div style={{ position: 'absolute', top: '5px', right: '20px' }}>
      <form onSubmit={handleLogin}> 
        <input
          type="text"
          name="username"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button> {/* Change to type="submit" */}
      </form>
      {error && <div className="error-messages">{error}</div>}
    </div>
  );

  const logoutButton = (
    <div style={{ position: 'absolute', top: '10px', right: '20px' }}>
      <button onClick={handleLogout}>Logout</button> {/* Corrected for logout */}
    </div>
  );

  return isLoggedIn ? logoutButton : loginForm;
}

export default Logging;
