// redux/reducers/authReducer.js

const initialState = {
    isLoggedIn: false,
    userToken: null,
    userDetails: {},
    is2FAVerified: false, // Add this if you're managing 2FA state in Redux
  };
  
  export default function authReducer(state = initialState, action) {
    switch (action.type) {
      case 'LOGIN_SUCCESS':
        return {
          ...state,
          isLoggedIn: true,
          userToken: action.payload.token,
          userDetails: action.payload.user,
          // Set is2FAVerified based on your app's logic
        };
      case 'LOGOUT':
        return {
          ...state,
          isLoggedIn: false,
          userToken: null,
          userDetails: {},
          is2FAVerified: false,
        };
      default:
        return state;
    }
  }
