import React, { useState } from 'react';
import authService from '../services/authservice';

function LoginComponent() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // To store login errors

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const data = await authService.login(username, password);
      setIsLoggedIn(true);
      localStorage.setItem('userToken', data.token); // Save the token
      setError(''); // Clear any previous error
      // Redirect or perform additional actions upon successful login
    } catch (error) {
      console.error("Login failed:", error);
      setError('Login failed. Please check your credentials.'); // Update error state to display message
      setIsLoggedIn(false); // Ensure user is marked as logged out on failure
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    // Clear user authentication tokens or cookies here
  };

  const loginForm = (
    <div style={{ position: 'absolute', top: '5px', right: '20px' }}>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
      
    </div>
  );

  const logoutButton = (
    <div style={{ position: 'absolute', top: '10px', right: '20px' }}>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );

  return isLoggedIn ? logoutButton : loginForm;
}

export default LoginComponent;
