const login = async (username, password) => {
    try {
      const response = await fetch('/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });
      
      if (!response.ok) {
        throw new Error('Login failed');
      }
  
      const data = await response.json();
      // Here you could store the received token or user details in localStorage, for example:
      // localStorage.setItem('userToken', data.token);
      
      return data; // This data should include the token or any other user info
    } catch (error) {
      console.error("Login error:", error);
      throw error; // Rethrowing the error is important for handling it in the calling component
    }
  };
  
  // Example logout function, assuming you store a token in localStorage
  const logout = () => {
    // Perform any necessary cleanup, like removing the user's token from storage
    localStorage.removeItem('userToken');
    // You might also want to redirect the user to the login page or home page here
  };
  
  const authService = { login, logout };
  
  export default authService;
