import React, { useState } from 'react';
import authService from '../services/authservice';
import { useNavigate } from 'react-router-dom';

function Logging() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState(''); // To store login errors
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      const data = await authService.login(username, password);
      setIsLoggedIn(true);
      localStorage.setItem('userToken', data.token);
      localStorage.setItem('userDetails', JSON.stringify(data.user));
      setError('');
      navigate('/profile');
      // Redirect or perform additional actions upon successful login
    } catch (error) {
      console.error("Login failed:", error);
      setError('Login failed. Please check your credentials.'); // Update error state to display message
      setIsLoggedIn(false); // Ensure user is marked as logged out on failure
    }
  };

  const handleLogout = async () => {
    await authService.logout(); 
    setIsLoggedIn(false);
    navigate('/home');
  };

  const loginForm = (
    <div style={{ position: 'absolute', top: '5px', right: '20px' }}>
      <form>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button onClick={handleLogin}>Login</button>
      </form>
      {error && <div className="error-messages">{error}</div>}
    </div>
  );

  const logoutButton = (
    <div style={{ position: 'absolute', top: '10px', right: '20px' }}>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );

  return isLoggedIn ? logoutButton : loginForm;
}

export default Logging;
