import React, { useState } from 'react';
import authService from '../services/authservice';

function LoginControl() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // For demonstration purposes
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const login = async (e) => {
    e.preventDefault();
    try {
      const user = await authService.login(username, password);
      setIsLoggedIn(true);
      // Further actions based on successful login
    } catch (error) {
      console.error("Login failed:", error);
      // Handle login failure
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    // Clear user authentication tokens or cookies here
  };

  const loginForm = (
    <div style={{ position: 'absolute', top: '5px', right: '20px' }}>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <button type="submit">Login</button>
      </form>
    </div>
  );

  const logoutButton = (
    <div style={{ position: 'absolute', top: '10px', right: '20px' }}>
      <button onClick={handleLogout}>Logout</button>
    </div>
  );

  return isLoggedIn ? logoutButton : loginForm;
}

export default LoginControl;
